public class Ej_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//2. Realizar un programa que muestre los números del 1 al 100 utilizando la instrucción while
		int i = 0;
		
		while(i<=100) {
			
			System.out.println(i);
			i++;
			
		}
		
		
	}

}
