package ejercicios;

public class Ej_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//3. Realizar un programa que muestre los números del 1 al 100 utilizando la instrucción do while

		//declaro valor de variable
		int joaquin = 0;
		
		//hago
		 do{
			 //imprimo valor de variable
			System.out.println(joaquin);
			
			//incremento el valor de la variable
			joaquin++;
		
		 }
		 //corto cuando la variable llegue al valor
		 while (joaquin<=100); 
		 
	
	}

}
